sap.ui.define([],function(){"use strict";var P={apiVersion:2,render:function(r,p){var g=p.getGroups(),n=g.length,G,i;r.openStart("div",p);r.class("sapUshellPage");r.openEnd();if(p.getEdit()&&!n){r.renderControl(p.getAggregation("_addGroupButtons")[0]);}if(!n&&p.getShowNoGroupsText()){r.renderControl(p.getAggregation("_noGroupText"));}for(i=0;i<n;i++){G=g[i];if(p.getEdit()){r.openStart("div");r.class("sapUshellPageGroup");r.attr("tabindex","0");r.openEnd();r.renderControl(G);r.close("div");r.renderControl(p.getAggregation("_addGroupButtons")[i+1]);}else{r.renderControl(G);}}r.close("div");}};return P;});
